mapper.select:
SELECT SEQ
FROM CHAT 
WHERE AND USERID = #{userid}

SELECT SEQ, USERID, CHATID, CONTENT, FILE, WDATE
FROM MESSAGE 
WHERE CHATID = #{seq}

SELECT SEQ, USERID, CHATID, CONTENT, FILE, WDATE
FROM MESSAGE 
WHERE SEQ = #{seq}

mapper.update:
UPDATE MESSAGE
SET READED = 0
WHERE CHATID = #{chatid}
	AND USERID != #{userid}
	AND READED = 1
